import React, { useEffect } from "react";
import BookResults from "../ResultList";
import DelBtn from "../DeleteButton";
import { useStoreContext } from "../../utils/GlobalState";
import API from "../../utils/API";
import axios from "axios";


function BookList() {
    const [state, dispatch] = useStoreContext();
   
    const removeBook = id => {
        API.deleteBook(id)
        .then(() =>{
            dispatch({
                type: "REMOVE_BOOK",
                _id: id
            });
        })
        .catch(err => console.log(err));
    };

    let bookData;
    console.log(state);
    useEffect(() => {
        axios.get("/books")
             .then(response => {
                bookData = response.data;
                dispatch({type: "ADD_BOOK", payload: bookData});
             })
    }, []);


    return (
        <div>
            <h1>Saved Books</h1>
            <h3>Click the X to remove a book</h3>
            {console.log(state)}
            {bookData ? (
                <BookResults 
                 books = {bookData} >
                    {/* <DelBtn onClick={() => removeBook(book._id)} /> */}
                </BookResults>
            ) : (
                <h3>You haven't added any books yet!</h3>
            )}
        </div>
    )
}

export default BookList;
