import React, { useRef } from "react";
import { useStoreContext } from "../../utils/GlobalState";

import API from "../../utils/API";

function CreateBookSearch() {
  const titleRef = useRef();
  const [state, dispatch] = useStoreContext();

  const handleSubmit = event => {
    event.preventDefault();
    dispatch({ type: "LOADING" });
    API.search({
      title: titleRef.current.value,
    })
      .then(result => {
          console.log(result);
        dispatch({
          type: "UPDATE_BOOK",
          post: result.data
        });
      }) 
      .catch(err => console.log(err));
      console.log(state);

    titleRef.current.value = "";
  };

  return (
    <div>
      <h1>Search for a book!</h1>
      <form className="form-group mt-5 mb-5" onSubmit={handleSubmit}>
        <input className="form-control mb-5" required ref={titleRef} placeholder="Title" />
        <button className="btn btn-success mt-3 mb-5" disabled={state.loading} type="submit">
          Search
        </button>
      </form>
      {/* <div>
            <h1>Saved Books</h1>
            <h3>Click the X to remove a book</h3>
            {state.saved[0] ? (
                <BookResults>
                    {state.saved[0].map(book => (
                    // console.log("Hi", book.title )
                        <strong>
                        {book.title}
                        {<DelBtn onClick={() => removeBook(book._id)} />}
                        </strong>
                    ))}
                    
                </BookResults>
            ) : (
                <h3>You haven't added any books yet!</h3>
            )}
            
            
        </div> */}
    </div>
  );
}

export default CreateBookSearch;
