import React, { useEffect } from "react";
import BookResults from "../ResultList";
import { ListItem, List } from "../ResultList";
import DelBtn from "../DeleteButton";
import { useStoreContext } from "../../utils/GlobalState";
import API from "../../utils/API";
import axios from "axios";



function BookList() {
    const [state, dispatch] = useStoreContext();
   
    const removeBook = id => {
        API.deleteBook(id)
        .then(() =>{
            dispatch({
                type: "REMOVE_BOOK",
                _id: id
            });
        })
        .catch(err => console.log(err));
    };

    console.log(state);
    
    
    useEffect(() => {
        API.getBooks("/books")
             .then(response => {
                
                // console.log("Response" + response)
                dispatch({type: "LOADING"});
                dispatch({type: "ADD_BOOK", payload: response});
                
                //console.log(state.saved[0].data);
            })
    }, []);
    


    return (
        // <div>
        //     <h1>Saved Books</h1>
        //     <h3>Click the X to remove a book</h3>
        //     {console.log(state.saved[0].data[0].title + "inside")}
            
            
        // </div>
        <div>
        <h1>All Blog Posts</h1>
        <h3 className="mb-5 mt-5">Click on a post to view</h3>
        {state.saved[0].data.length ? (
          <List>
            {state.saved[0].data.map(post => (
              <ListItem key={book._id}>
                
                  <strong>
                    {book.title} by {book.author}
                  </strong>
                
                {/* <DeleteBtn onClick={() => removePost(post._id)} /> */}
              </ListItem>
            ))}
          </List>
        ) : (
          <h3>You haven't added any posts yet!</h3>
        )}
        <div className="mt-5">
         
        </div>
      </div>
    )
}

export default BookList;
