import React, { useEffect } from "react";
import BookResults from "../ResultList";
// import { ListItem, List } from "../ResultList";
// import DelBtn from "../DeleteButton";
import { useStoreContext } from "../../utils/GlobalState";
import API from "../../utils/API";




function BookList() {
    const [state, dispatch] = useStoreContext();
    
    // const removeBook = id => {
    //     API.deleteBook(id)
    //     .then(() =>{
    //         dispatch({
    //             type: "REMOVE_BOOK",
    //             _id: id
    //         });
    //     })
    //     .catch(err => console.log(err));
    // };

    console.log(state);
    
    
    useEffect(() => {
        API.getBooks("/books")
             .then(response => {
                
                // console.log("Response" + response)
                // dispatch({type: "LOADING"});
                dispatch({type: "ADD_BOOK", payload: response});
                
                console.log(state.saved[0].data );
            })
    }, []);
    


    return (
        <div>
            <h1>Saved Books</h1>
            <h3>Click the X to remove a book</h3>
            {console.log(state.currentBook + "inside")}
            {state.saved[0].data.length ? (
            <BookResults>
                {console.log("Hi " )}
                {state.saved[0].data.map(book => (
                    <strong>
                    {book.title}
                    </strong>
                ))}
                {/* <DelBtn onClick={() => removeBook(book._id)} /> */}
            </BookResults>
        ) : (
            <h3>You haven't added any books yet!</h3>
        )}
            
            
        </div>
        
        
    )
}

export default BookList;
